mapper.select:
SELECT pi.PROCESS_INSTANCE_KEY, pi.BPMN_PROCESS_ID, pi.PROCESS_DEFINITION_KEY, pi.STATE, pi.START_DATE, pi.END_DATE, pi.TENANT_ID, pi.PARENT_PROCESS_INSTANCE_KEY, pi.PARENT_ELEMENT_INSTANCE_KEY, pi.VERSION FROM PROCESS_INSTANCE pi WHERE 1 = 1
AND BPMN_PROCESS_ID = #{bpmnProcessId}
AND pi.PROCESS_INSTANCE_KEY IN (SELECT v.PROCESS_INSTANCE_KEY FROM VARIABLE v WHERE v.VAR_NAME = #{variable.name} AND v.VAR_VALUE IN
CAST(#{variableValue} as VARCHAR)
)

mapper.update:
UPDATE PROCESS_INSTANCE p
SET STATE    = #{state},
    END_DATE = #{endDate}
WHERE PROCESS_INSTANCE_KEY = #{processInstanceKey}
